import React, {Component} from 'react';
import NewItem from './NewItem';
import Spinner from './Spinner';
import PropTypes from 'prop-types';
import InfiniteScroll from "react-infinite-scroll-component";

export default class News extends Component {
  static defaultProps = {
    country: 'us',
    pageSize: 5,
    category: 'general',
  };
  static propTypes = {
    country: PropTypes.string,
    pageSize: PropTypes.number,
    category: PropTypes.string,
  };
 capitalize =(s)=>
{
    return s[0].toUpperCase() + s.slice(1);
}
  constructor (props) {
    super (props);
    this.state = {
      articles: [],
      loading: false,
      page: 1,
      totalResults : 0,
    };
    document.title= `${this.capitalize(this.props.category)} - News `;
  }

  async updateNews () {
    const url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=1eb2afab58ca4f71b57f3776602d79d2&page=1&pageSize=${this.props.pageSize}`;
    this.setState ({loading: true});
    let data = await fetch (url);
    let parsedData = await data.json ();
    console.log (parsedData);
    this.setState ({
      articles: parsedData.articles,
      totalResults: parsedData.totalResults,
      loading: false,
    });
  }
  async componentDidMount () {
    this.updateNews ();
  }
  handlePrevclick = async () => {
    this.setState ({page: this.state.page - 1});
    this.updateNews ();
  };

  handleNextclick = async () => {
    this.setState ({page: this.state.page - 1});
    this.updateNews ();
  };
  fetchMoreData = async() => {
    this.setState({page : this.state.page + 1})
    const url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=1eb2afab58ca4f71b57f3776602d79d2&page=1&pageSize=${this.props.pageSize}`;
    this.setState ({loading: true});
    let data = await fetch (url);
    let parsedData = await data.json ();
    console.log (parsedData);
    this.setState ({
      articles: this.state.articles.concat (parsedData.articles),
      totalResults: parsedData.totalResults,
      loading: false,
    });

  };
  render () {
    return (
     <>
        <h1 className="text-center">News Top {this.capitalize(this.props.category)} Headlines</h1>
        {/* {this.state.loading && <Spinner />} */}
        <InfiniteScroll
          dataLength={this.state.articles.length}
          next={this.fetchMoreData}
          hasMore={this.state.articles.length !== this.state.totalResults}
          loader={<Spinner/>}
        >
          <div className="container">
        <div className="row">
          {this.state.articles.map ((element) => {
              return (
                <div className="col-md-4"> key={elem}
                  <NewItem
                    title={element.title ? element.title.slice (0, 45) : ''}
                    description={
                      element.description
                        ? element.description.slice (0, 88)
                        : ''
                    }
                    imageUrl={element.urlToImage ? element.urlToImage : ''}
                    newsUrl={element.url}
                    author={element.author}
                    date={element.publishedAt}
                    source={element.source.name}
                  />
                </div>
              );
            })}
        </div>
        </div>
        </InfiniteScroll>
       
      </>
    );
  }
}
