import React, { Component } from "react";
import NewItem from "./NewItem";
import Spinner from "./Spinner";

export default class News extends Component {
  constructor() {
    super();
    console.log("Hello i am a constructor");
    this.state = {
      articles: [],
      loading: false,
      page: 1,
    };
  }
  async componentDidMount() {
    console.log("cmd");
    let url =
      "https://newsapi.org/v2/everything?q=cricket&from=2023-03-02&to=2023-03-02&sortBy=popularity&apiKey=1eb2afab58ca4f71b57f3776602d79d2&page=1&pageSize=5";
    let data = await fetch(url);
    let parsedData = await data.json();
    console.log(parsedData);
    this.setState({ articles: parsedData.articles });
  }
  handlePrevclick = async () => {
    console.log("Previous");
    if (
      this.state.page + 1 >
      Math.ceil(this.state.totalResults / this.props.pageSize)
    ) {
    } else {
      let url = `https://newsapi.org/v2/everything?q=cricket&from=2023-03-02&to=2023-03-02&sortBy=popularity&apiKey=1eb2afab58ca4f71b57f3776602d79d2&page=${
        this.state.page - 1
      }&pageSize=5`;
      let data = await fetch(url);
      let parsedData = await data.json();
      console.log(parsedData);
      this.setState({
        page: this.state.page - 1,
        articles: parsedData.articles,
      });
    }
  };
  handleNextclick = async () => {
    console.log("Next");
    if (
      !(
        this.state.page + 1 >
        Math.ceil(this.state.totalResults / this.props.pageSize)
      )
    ) {
      let url = `https://newsapi.org/v2/everything?q=cricket&from=2023-03-02&to=2023-03-02&sortBy=popularity&apiKey=1eb2afab58ca4f71b57f3776602d79d2&page=${
        this.state.page + 1
      }&pageSize=5`;
      this.setState({loading:true});
      let data = await fetch(url);
      let parsedData = await data.json();
      console.log(parsedData);
      this.setState({
        page: this.state.page + 1,
        articles: parsedData.articles,
      });
    }
  };
  render() {
    return (
      <div className="container my-2">
        <h1 className="text-center">NewsMonkey Top Headlines</h1>
        {this.state.loading && <Spinner />}
        <div className="row">
          {this.state.articles.map((element) => {
            return (
              <div className="col-md-4">
                <NewItem
                  title={element.title ? element.title.slice(0, 45) : ""}
                  description={
                    element.description ? element.description.slice(0, 88) : ""
                  }
                  imageUrl={element.urlToImage ? element.urlToImage : ""}
                  newsUrl={element.url}
                />
              </div>
            );
          })}
        </div>
        <div className="container d-flex justify-content-between">
          <button
            disabled={this.state.page <= 1}
            type="button"
            className="btn btn-dark"
            onClick={this.handlePrevclick}
          >
            &larr; Previous
          </button>
          <button
            type="button"
            className="btn btn-dark"
            onClick={this.handleNextclick}
          >
            {" "}
            Next &rarr;
          </button>
        </div>
      </div>
    );
  }
}
