import React, {Component} from 'react';
import NewItem from './NewItem';
import Spinner from './Spinner';
import PropTypes from 'prop-types';

export default class News extends Component {
  static defaultProps = {
    country: 'us',
    pageSize: 5,
    category: 'general',
  };
  static propTypes = {
    country: PropTypes.string,
    pageSize: PropTypes.number,
    category: PropTypes.string,
  };
 capitalize =(s)=>
{
    return s[0].toUpperCase() + s.slice(1);
}
  constructor (props) {
    super (props);
    this.state = {
      articles: [],
      loading: false,
      page: 1,
    };
    document.title= `${this.capitalize(this.props.category)} - News `;
  }

  async updateNews () {
    const url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=1eb2afab58ca4f71b57f3776602d79d2&page=1&pageSize=${this.props.pageSize}`;
    this.setState ({loading: true});
    let data = await fetch (url);

    let parsedData = await data.json ();

    console.log (parsedData);
    this.setState ({
      articles: parsedData.articles,
      totalResults: parsedData.totalResults,
      loading: false,
    });
  }
  async componentDidMount () {
    this.updateNews ();
  }
  handlePrevclick = async () => {
    this.setState ({page: this.state.page - 1});
    this.updateNews ();
  };

  handleNextclick = async () => {
    this.setState ({page: this.state.page - 1});
    this.updateNews ();
  };
  render () {
    return (
      <div className="container my-2">
        <h1 className="text-center">News Top {this.capitalize(this.props.category)} Headlines</h1>
        {/* {this.state.loading && <Spinner />} */}
        <div className="row">
          {this.state.articles.map ((element) => {
              return (
                <div className="col-md-4">
                  <NewItem
                    title={element.title ? element.title.slice (0, 45) : ''}
                    description={
                      element.description
                        ? element.description.slice (0, 88)
                        : ''
                    }
                    imageUrl={element.urlToImage ? element.urlToImage : ''}
                    newsUrl={element.url}
                    author={element.author}
                    date={element.publishedAt}
                    source={element.source.name}
                  />
                </div>
              );
            })}
        </div>
        <div className="container d-flex justify-content-between">
          <button
            disabled={this.state.page <= 1}
            type="button"
            className="btn btn-dark"
            onClick={this.handlePrevclick}
          >
            &larr; Previous
          </button>
          <button type="button"className="btn btn-dark"onClick={this.handleNextclick}>
            {' '}Next &rarr;
          </button>
        </div>
      </div>
    );
  }
}
